sys_ux_client_script.script:
function handler({api}) {
    console.log('===== Client script loaded! =====');
    console.log('API context:', api.context);
    console.log('API context.host:', api.context?.host);

    try {
        console.log('Searching for buttons...');

        // api.context.hostを取得
        const host = api.context?.host;
        console.log('Host element:', host);

        if (host && host.querySelectorAll) {
            console.log('Host has querySelectorAll');
            const buttons = host.querySelectorAll('now-button, button, [role="button"]');
            console.log('Buttons found:', buttons.length);

            buttons.forEach((button, index) => {
                const text = button.textContent || '';
                console.log('Button ' + index + ':', text.trim());
            });
        } else {
            console.error('Host is null or does not have querySelectorAll');
            console.log('Trying window.document instead...');

            const buttons = window.document.querySelectorAll('now-button, button');
            console.log('Buttons from window.document:', buttons.length);
        }

    } catch (err) {
        console.error('Error:', err);
        console.error('Error stack:', err.stack);
    }

    console.log('===== Script finished =====');
}
